            {
                "Protocol": "TCP", 
                "Port": "80", 
                "CidrBlock": "0.0.0.0/0", 
                "Action": "ACCEPT", 
                "PolicyDescription": "80端口2"
            }, 
            {
                "Protocol": "TCP", 
                "Port": "21", 
                "CidrBlock": "0.0.0.0/0", 
                "Action": "ACCEPT", 
                "PolicyDescription": "ftp端口1"
            }
        ]
    }
